import React, { Component } from "react";
import StudentForm from "../components/studentForm";
import { connect } from "react-redux";
import { getStudents } from "../actions";
import { Link } from "react-router-dom";
class App extends Component {
  componentDidMount() {
    this.props.getStudents();
    // console.log(this.state);
  }

  renderHeader() {
    return (
      <table class="table">
        <thead>
          <tr>
            <th scope="col-sm-4">Active</th>
            <th scope="col-sm-4">Delinquent</th>
            <th scope="col-sm-4">Dropped</th>
          </tr>
        </thead>
      </table>
    );
  }
  renderActiveColumn() {
    return this.props.students.map(student => {
      if (student.status === "active") {
        return (
          <Link
            to={`/edit/${student.id}`}
            className="card"
            to=""
            style={{ borderColor: "Green" }}
          >
            <ul class="list-group list-group-flush ">
              <li class="list-group-item ">
                {student.firstName}
                {student.lastName}
              </li>
              <li class="list-group-item">Student ID: {student.id}</li>
              <li class="list-group-item">
                Phone Number:{student.phoneNumber}
              </li>
            </ul>
          </Link>
        );
      }
    });
  }
  renderDelinquentColumn() {
    return this.props.students.map(student => {
      if (student.status === "delinquent") {
        return (
          <Link
            to={`/edit/${student.id}`}
            className="card "
            style={{ borderColor: "Orange" }}
          >
            <ul class="list-group list-group-flush ">
              <li class="list-group-item ">
                {student.firstName}
                {student.lastName}
              </li>
              <li class="list-group-item">Student ID: {student.id}</li>
              <li class="list-group-item">
                Phone Number:{student.phoneNumber}
              </li>
            </ul>
          </Link>
        );
      }
    });
  }
  renderDroppedColumn() {
    return this.props.students.map(student => {
      if (student.status === "dropped") {
        return (
          <Link
            to={`/edit/${student.id}`}
            className="card "
            style={{ borderColor: "Red" }}
          >
            <ul class="list-group list-group-flush ">
              <li class="list-group-item ">
                {student.firstName}
                {student.lastName}
              </li>
              <li class="list-group-item">Student ID: {student.id}</li>
              <li class="list-group-item">
                Phone Number:{student.phoneNumber}
              </li>
            </ul>
          </Link>
        );
      }
    });
  }

  renderLists() {
    return (
      <div className="container">
        <div>{this.renderHeader()}</div>
        <div className="col-md-4" style={{ float: "left" }}>
          {this.renderActiveColumn()}
        </div>
        <div className="col-md-4 " style={{ float: "left" }}>
          {this.renderDelinquentColumn()}
        </div>
        <div className="col-md-4" style={{ float: "left" }}>
          {this.renderDroppedColumn()}
        </div>
      </div>
    );
  }
  render() {
    console.log(this.props.students);
    return (
      <div className="container">
        <div class="row">
          <h2>Home</h2>
          <Link style={{ marginLeft: "85%" }} to="/addStudent">
            <button className="btn btn-secondary">Add a student</button>
          </Link>
        </div>
        <div>{this.renderLists()}</div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log(state);
  return { students: Object.values(state.students) };
};

export default connect(
  mapStateToProps,
  { getStudents }
)(App);
