import React, { Component } from "react";
import StudentForm from "../components/studentForm";
import { connect } from "react-redux";
import { getStudents } from "../actions";
class App extends Component {
  componentDidMount() {
    this.props.getStudents();
    // console.log(this.state);
  }
  renderTitle() {
    return (
      <div class="row">
        <div class="col-sm-4">Delinquent</div>
        <div class="col-sm-4">Active</div>
        <div class="col-sm-4">Dropped</div>
      </div>
    );
  }
  renderList() {
    return this.props.students.map(student => {
      switch (student.status) {
        case "active":
        case "delinquent":
        case "dropped":
        default:
          return student.status;
      }
      return (
        <table class="table">
          <thead>
            <tr>
              <th scope="col">Delinquent</th>
              <th scope="col">Active</th>
              <th scope="col">Dropped</th>
            </tr>
          </thead>
          <div className="card col-sm-4">
            <ul class="list-group list-group-flush ">
              <li class="list-group-item ">
                {student.firstName}
                {student.lastName}
              </li>
              <li class="list-group-item">Student ID: {student.id}</li>
              <li class="list-group-item">
                Phone Number:{student.phoneNumber}
              </li>
            </ul>
          </div>
        </table>
      );
    });
  }

  render() {
    console.log(this.props.students);
    return (
      <div className="container">
        <h2>Home page</h2>
        <div>{this.renderTitle()}</div>
        <div>{this.renderList()}</div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log(state);
  return { students: Object.values(state.students) };
};

export default connect(
  mapStateToProps,
  { getStudents }
)(App);
