import React, { Component } from "react";
import { connect } from "react-redux";
import { getStudents } from "../actions";
import { Link } from "react-router-dom";
class App extends Component {
  state = {
    numberOfRecords: 5
  };

  componentDidMount() {
    this.props.getStudents();
  }

  renderHeader() {
    return (
      <div className="row" style={{ margin: "10px" }}>
        <div
          className="col-4"
          style={{
            textAlign: "center",
            padding: "20px",
            backgroundColor: "#ABEBC6"
          }}
        >
          <h4>Active</h4>
        </div>
        <div
          className="col-4"
          style={{
            textAlign: "center",
            padding: "20px",
            backgroundColor: "#FAD7A0"
          }}
        >
          <h4>Delinquent</h4>
        </div>
        <div
          className="col-4"
          style={{
            textAlign: "center",
            padding: "20px",
            backgroundColor: "#F1948A"
          }}
        >
          <h4>Dropped</h4>
        </div>
      </div>
    );
  }
  renderActiveColumn() {
    let counter = 0;
    return this.props.students.map(student => {
      if (student.status === "active") {
        //counter += 1;
      }
      if (
        counter <= this.state.numberOfRecords &&
        student.status === "active"
      ) {
        return (
          <div
            to={`/edit/${student._id}`}
            className="card"
            style={{ borderColor: "Green", margin: "5px" }}
            key={student._id}
          >
            <ul className="list-group  ">
              <Link to={`/edit/${student.id}`}>
                <li className="list-group-item ">
                  <i className="user icon" />
                  {student.firstName}
                  {student.lastName}
                </li>
                <li className="list-group-item">
                  <i className="id card icon" />
                  {student.id}
                </li>
                <li className="list-group-item">
                  <i className="phone icon" /> {student.phoneNumber}
                </li>
              </Link>
              <li className="list-group-item">
                <Link
                  to={`/delete/${student.id}`}
                  className="right floated content button ui negative"
                >
                  Delete
                </Link>
              </li>
            </ul>
          </div>
        );
      }
      return null;
    });
  }
  renderDelinquentColumn() {
    let counter = 0;
    return this.props.students.map(student => {
      if (student.status === "delinquent") {
        counter += 1;
      }
      if (
        counter <= this.state.numberOfRecords &&
        student.status === "delinquent"
      ) {
        return (
          <div
            className="card"
            style={{ borderColor: "Orange", margin: "5px" }}
            key={student.id}
          >
            <ul className="list-group  ">
              <Link to={`/edit/${student.id}`}>
                <li className="list-group-item ">
                  <i className="user icon" />
                  {student.firstName}
                  {student.lastName}
                </li>
                <li className="list-group-item">
                  <i className="id card icon" />
                  {student.id}
                </li>
                <li className="list-group-item">
                  <i className="phone icon" />
                  {student.phoneNumber}
                </li>
              </Link>
              <li className="list-group-item">
                <Link
                  to={`/delete/${student.id}`}
                  className="right floated content button ui negative"
                >
                  Delete
                </Link>
              </li>
            </ul>
          </div>
        );
      }
      return null;
    });
  }
  renderDroppedColumn() {
    let counter = 0;
    return this.props.students.map(student => {
      if (student.status === "dropped") {
        counter += 1;
        console.log(counter);
      }
      if (
        counter <= this.state.numberOfRecords &&
        student.status === "dropped"
      ) {
        return (
          <div
            className="card"
            style={{ borderColor: "Red", margin: "5px" }}
            key={student.id}
          >
            <ul className="list-group  ">
              <Link to={`/edit/${student.id}`}>
                <li className="list-group-item ">
                  <i className="user icon" />
                  {student.firstName}
                  {student.lastName}
                </li>
                <li className="list-group-item">
                  <i className="id card icon" />
                  {student.id}
                </li>
                <li className="list-group-item">
                  <i className="phone icon" />
                  {student.phoneNumber}
                </li>
              </Link>
              <li className="list-group-item">
                <Link
                  to={`/delete/${student.id}`}
                  className="right floated content button ui negative"
                >
                  Delete
                </Link>
              </li>
            </ul>
          </div>
        );
      }
      return null;
    });
  }

  renderLists() {
    return (
      <div className="ui container">
        <div>{this.renderHeader()}</div>
        <div className="col-4" style={{ float: "left" }}>
          {this.renderActiveColumn()}
        </div>
        <div className="col-4 " style={{ float: "left" }}>
          {this.renderDelinquentColumn()}
        </div>
        <div className="col-4" style={{ float: "left" }}>
          {this.renderDroppedColumn()}
        </div>
      </div>
    );
  }
  render() {
    return (
      <div style={{ backgroundColor: "#fff" }}>
        <div className="container">
          <div className="row">
            <h2>Home</h2>

            <Link style={{ marginLeft: "85%" }} to="/addStudent">
              <button className="btn btn-secondary">
                <ion-icon name="add-circle-outline">Add a student</ion-icon>
              </button>
            </Link>
          </div>
          <div>{this.renderLists()}</div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  // console.log(state);
  return { students: Object.values(state.students) };
};

export default connect(
  mapStateToProps,
  { getStudents }
)(App);
