  submit = values => {
    console.log(values);
  };

  render() {
    return (
      <div>
        <div>{this.props.students}</div>
        <div>
          <StudentForm onSubmit={this.submit} />
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log(state);
