import {
  ADD_STUDENT,
  // DELETE_STUDENT,
  // EDIT_STUDENT,
  // GET_STUDENT,
  GET_STUDENTS
} from "../actions/types";
import _ from "lodash";
export default (state = {}, action) => {
  console.log(action);
  console.log({ ...state, ..._.mapKeys(action.payload, "id") });
  switch (action.type) {
    case GET_STUDENTS:
      return { ...state, ..._.mapKeys(action.payload, "id") };
    case ADD_STUDENT:
      return { ...state, [action.payload.id]: action.payload };
    default:
      return state;
  }
};

