import React, { Component } from "react";
import StudentForm from "./studentForm";
import { editStudent, getStudents } from "../actions";
import { connect } from "react-redux";
class AddStudent extends Component {
  componentDidMount() {
    this.props.getStudents();
  }
  onSubmit = formValues => {
    this.props.editStudent(this.props.match.params.id, formValues);
  };

  render() {
    return (
      <div className="container">
        <h2>Student</h2>
        <StudentForm onSubmit={this.onSubmit} />
      </div>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  return { currentStudent: state.students[ownProps.match.params.id] };
};
export default connect(
  mapStateToProps,
  { editStudent, getStudents }
)(AddStudent);
