import {
  // ADD_STUDENT,
  // DELETE_STUDENT,
  // EDIT_STUDENT,
  // GET_STUDENT,
  GET_STUDENTS
} from "./types";
import axios from "axios";
const baseUrl = "http://localhost:3001";
const getStudents = students => {
  return {
    type: GET_STUDENTS,
    students
  };
};

export const getAllStudents = () => {
  return dispatch => {
    return axios
      .get(baseUrl)
      .then(response => {
        //console.log(response);
        dispatch(getStudents(response.data));
      })
      .catch(error => {
        throw error;
      });
  };
};

