import {
  ADD_STUDENT,
  DELETE_STUDENT,
  EDIT_STUDENT,
  GET_STUDENT,
  GET_STUDENTS
} from "../actions/types";

export default (state = {}, action) => {
  console.log(state);
  console.log(action);
  switch (action.type) {
    case GET_STUDENTS:
      return [...state, ..._.mapKeys(action.students, "_id")];
    default:
      return state;
  }
};

