import React, { Component } from "react";
import StudentForm from "../components/studentForm";
import { connect } from "react-redux";
import { getStudents } from "../actions";
import { Link } from "react-router-dom";
class App extends Component {
  componentDidMount() {
    this.props.getStudents();
    // console.log(this.state);
  }

  // switch (student.status) {
  //   case "active":
  //   case "delinquent":
  //   case "dropped":
  //   default:
  //     return student.status;
  // }
  renderHeader() {
    return (
      <table class="table">
        <thead>
          <tr>
            <th scope="col-sm-4">Active</th>
            <th scope="col-sm-4">Delinquent</th>
            <th scope="col-sm-4">Dropped</th>
          </tr>
        </thead>
      </table>
    );
  }
  renderActiveColumn() {
    return this.props.students.map(student => {
      if (student.status === "active") {
        return (
          <Link className="card" to="">
            <ul class="list-group list-group-flush ">
              <li class="list-group-item ">
                {student.firstName}
                {student.lastName}
              </li>
              <li class="list-group-item">Student ID: {student.id}</li>
              <li class="list-group-item">
                Phone Number:{student.phoneNumber}
              </li>
            </ul>
          </Link>
        );
      }
    });
  }
  renderDelinquentColumn() {
    return this.props.students.map(student => {
      if (student.status === "delinquent") {
        return (
          <Link className="card " style={{ borderColor: "Blue" }}>
            <ul class="list-group list-group-flush ">
              <li class="list-group-item ">
                {student.firstName}
                {student.lastName}
              </li>
              <li class="list-group-item">Student ID: {student.id}</li>
              <li class="list-group-item">
                Phone Number:{student.phoneNumber}
              </li>
            </ul>
          </Link>
        );
      }
    });
  }
  renderDroppedColumn() {
    return this.props.students.map(student => {
      if (student.status === "dropped") {
        return (
          <Link className="card ">
            <ul class="list-group list-group-flush ">
              <li class="list-group-item ">
                {student.firstName}
                {student.lastName}
              </li>
              <li class="list-group-item">Student ID: {student.id}</li>
              <li class="list-group-item">
                Phone Number:{student.phoneNumber}
              </li>
            </ul>
          </Link>
        );
      }
    });
  }

  renderLists() {
    return (
      <div className="container">
        <div>{this.renderHeader()}</div>
        <div className="col-md-4" style={{ float: "left" }}>
          {this.renderActiveColumn()}
        </div>
        <div className="col-md-4 " style={{ float: "left" }}>
          {this.renderDelinquentColumn()}
        </div>
        <div className="col-md-4" style={{ float: "left" }}>
          {this.renderDroppedColumn()}
        </div>
      </div>
    );
  }
  render() {
    console.log(this.props.students);
    return (
      <div className="container">
        <h2>Home page</h2>

        <div>{this.renderLists()}</div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log(state);
  return { students: Object.values(state.students) };
};

export default connect(
  mapStateToProps,
  { getStudents }
)(App);
