import {
  ADD_STUDENT,
  // DELETE_STUDENT,
  // EDIT_STUDENT,
  // GET_STUDENT,
  GET_STUDENTS
} from "./types";
import axios from "axios";
const baseUrl = "http://localhost:3001/students";

export const addStudent = formValues => async (dispatch, getState) => {
  console.log(getState());
  const response = await axios.post(baseUrl, formValues);
  // dispatch an action in the store
  dispatch({ type: ADD_STUDENT, payload: response.data });
};

