import React, { Component } from "react";
import { connect } from "react-redux";
import { getStudents } from "../actions";
import { Link } from "react-router-dom";
class App extends Component {
  componentDidMount() {
    this.props.getStudents();
    // console.log(this.state);
  }

  renderHeader() {
    return (
      <div className="row">
        <div className="col-sm-4" style={{ textAlign: "center" }}>
          <h5>Active</h5>
        </div>
        <div className="col-sm-4" style={{ textAlign: "center" }}>
          <h5>Delinquent</h5>
        </div>
        <div className="col-sm-4" style={{ textAlign: "center" }}>
          <h5>Dropped</h5>
        </div>
      </div>
    );
  }
  renderActiveColumn() {
    return this.props.students.map(student => {
      if (student.status === "active") {
        return (
          <Link
            to={`/edit/${student.id}`}
            className="card"
            style={{ borderColor: "Green" }}
            key={student.id}
          >
            <ul className="list-group list-group-flush ">
              <li className="list-group-item ">
                {student.firstName}
                {student.lastName}
              </li>
              <li className="list-group-item">Student ID: {student.id}</li>
              <li className="list-group-item">
                Phone Number:{student.phoneNumber}
              </li>
            </ul>
          </Link>
        );
      }
      return null;
    });
  }
  renderDelinquentColumn() {
    return this.props.students.map(student => {
      if (student.status === "delinquent") {
        return (
          <Link
            to={`/edit/${student.id}`}
            className="card "
            style={{ borderColor: "Orange" }}
            key={student.id}
          >
            <ul className="list-group list-group-flush ">
              <li className="list-group-item ">
                {student.firstName}
                {student.lastName}
              </li>
              <li className="list-group-item">Student ID: {student.id}</li>
              <li className="list-group-item">
                Phone Number:{student.phoneNumber}
              </li>
            </ul>
          </Link>
        );
      }
      return null;
    });
  }
  renderDroppedColumn() {
    return this.props.students.map(student => {
      if (student.status === "dropped") {
        return (
          <Link
            to={`/edit/${student.id}`}
            className="card "
            style={{ borderColor: "Red" }}
            key={student.id}
          >
            <ul className="list-group list-group-flush ">
              <li className="list-group-item ">
                {student.firstName}
                {student.lastName}
              </li>
              <li className="list-group-item">Student ID: {student.id}</li>
              <li className="list-group-item">
                Phone Number:{student.phoneNumber}
              </li>
            </ul>
          </Link>
        );
      }
      return null;
    });
  }

  renderLists() {
    return (
      <div className="container">
        <div>{this.renderHeader()}</div>
        <div className="col-md-4" style={{ float: "left" }}>
          {this.renderActiveColumn()}
        </div>
        <div className="col-md-4 " style={{ float: "left" }}>
          {this.renderDelinquentColumn()}
        </div>
        <div className="col-md-4" style={{ float: "left" }}>
          {this.renderDroppedColumn()}
        </div>
      </div>
    );
  }
  render() {
    return (
      <div className="container">
        <div className="row">
          <h2>Home</h2>
          <Link style={{ marginLeft: "85%" }} to="/addStudent">
            <button className="btn btn-secondary">Add a student</button>
          </Link>
        </div>
        <div>{this.renderLists()}</div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  // console.log(state);
  return { students: Object.values(state.students) };
};

export default connect(
  mapStateToProps,
  { getStudents }
)(App);
